import React from 'react';

import { Route } from 'react-router-dom';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import CollectionOverview from '../../components/collections-overview/collections-overview.components';
import CollectionPage from '../../pages/collection/collection.component';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

import { selectIsCollectionFetching } from '../../redux/shop/shop.selector';
import { fetchCollectionStartAsync } from '../../redux/shop/shop.actions';

const CollectionOverviewWithSpinner = WithSpinner(CollectionOverview);

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

    componentDidMount() {
        const { fetchCollectionStartAsync } = this.props;
        fetchCollectionStartAsync();
    }

    render() {
        const { match, isCollectionFetching } = this.props
        return (
            <div className='shop-page'>
                <Route exact path={`${match.path}`} render={(props) =>
                    <CollectionOverviewWithSpinner isLoading={isCollectionFetching} {...props} />}
                />
                <Route path={`${match.path}/:collectionId`} render={(props) =>
                    <CollectionPageWithSpinner isLoading={isCollectionFetching} {...props} />}
                />
            </div>
        );
    }
}

const mapPropToState = createStructuredSelector({
    isCollectionFetching: selectIsCollectionFetching
});
const mapDispatchToPtops = dispatch => ({
    fetchCollectionStartAsync: () => dispatch(fetchCollectionStartAsync())
});
export default connect(mapPropToState,
    mapDispatchToPtops)(ShopPage);
